INSERT INTO `wp_events` VALUES (43, 0, '2010-02-10 09:00:00', '2010-02-10 12:00:00', '7112 N. Fresno Street, Ste. 450', 'Fresno', 'CA', 93720, '', 'Establishing Performance Expectations', 'Achieving the results you need from employees in both quality and productivity depends largely on how clearly those expectations are defined and how consistently you hold employees accountable for meeting them. From orienting a new employee, to delegating an assignment or adding new responsibilities, these skills will help avoid costly misunderstandings and ensure that the highest level of business standards and priorities are met.', 'Brenda Budke', 'budke@localhost:8080', '559.256.5011', 1, '', 0, 0);
