INSERT INTO `wp_events` VALUES (88, 0, '2011-09-20 09:00:00', '2011-09-20 12:00:00', '7447 N. First Street, Suite 103', 'Fresno', 'CA', 93720, '', 'Leadership Academy - Resolving Team Conflicts', 'We invite business owners, managers, attorneys and human resource professionals to attend on-site training to improve productivity and morale. Join us and learn practical, proactive and legal strategies to effectively manage your workforce.  \r\n\r\nIn today’s competitive economy working under deadline pressures with limited resources, it is inevitable that conflicts and disputes between diverse co-workers will arise. This workshop prepares supervisors to deal with these conflicts as they arise, rather than allow them to build up and spill over onto other workers. Supervisors and team leaders who attend will be able to:\r\n\r\n	Explain the team’s purpose and impact on the organization.\r\n	Describe the results and standards the team is expected to achieve.\r\n	Determine the roles and responsibilities of each team member.\r\n	Identify procedures and resources for getting the work done.\r\n	Deliver encouragement to team members to enable them to help each other.\r\n \r\n\r\nRegistration:	$79.00 per person ($69 for HR Business Partners)  \r\n\r\nTo register, please contact our receptionist at (559) 431-8090, via fax at (559) 437-0500; or via e-mail to stefanopoulos@localhost:8080.  Sign up now!  Seating is limited.  No refunds or cancellations; however, substitutions will be accepted.\r\n', 'Lauren Stefanopoulos', 'stefanopoulos@sierrahr.com', '559.431.8090', 1, '', 0, 1);
INSERT INTO `wp_events` VALUES (89, 0, '2011-10-13 07:30:00', '2011-10-13 15:00:00', 'Holiday Inn, Downtown Fresno', 'Fresno', 'CA', 0, '', '12th Annual Central Valley Human Resource and Safety Conference', 'Come join us for a day of networking and learning! Topics will include terrorism, the ADA, employee on-boarding, workers'' compensation fraud and disciplining employees for use of social media.\r\n\r\nAgenda, Speakers, Topics and Registration will be posted at www.hraccfresno.org.\r\n\r\nVendors are welcome, please contact Dawn Cagle at dawn.cagle@fresnoeoc.org', '', '', '', 1, '', 0, 0);
