INSERT INTO `wp_eventscalendar_main` VALUES (21, 'Leadership Academy: Basic Principles of a Collaborative Workplace', 'We invite business owners, supervisors, attorneys and human resource professionals to attend this workshop that illustrates how five Basic Principles can create a collaborative climate where everyone is able to cooperate, share ideas and work together for a common purpose.  The Basic Principles are a set of behaviors that help individuals at every level of an organization, put shared values into practice to develop strong work relationships and a high performance workplace culture.\r\n\r\nThis interactive workshop will help participants:\r\n• Describe how collaboration improves organizational performance\r\n• Explain the importance of shared values in creating a collaborative workplace\r\n• Identify the values that are important to them and their organization\r\n• Explain how the Basic Principles help put shared values into practice\r\n• Understand that leadership starts with them, regardless of their position or title\r\n• Learn how to be proactive, rather than reactive\r\n• Identify specific ways they can use the Basic Principles in their work situations\r\n', 'Sierra HR Partners, Inc.', '', '2013-03-20', '09:00:00', '2013-03-20', '12:00:00', 'public', 0);
