INSERT INTO `wp_eventscalendar_main` VALUES (54, 'Leadership Academy: Dealing with Creative Communication Styles ', 'This seminar will provide an interactive overview of four basic communication styles, as well as principles from Stephen Covey\\''s Habit 5: Seek First to Understand, Then to be Understood.  This workshop will also help participants determine communication style tendencies, understand how presumptions and assumptions impact communication, identify autobiographical responses and how they interfere with effective listening, along with much more. Join us and improve your essential communication and listening skills!', 'Sierra HR Offices 7447 N. First St., Ste. 103, Fresno, CA 93720', '', '2014-05-13', '09:00:00', '2014-05-13', '12:00:00', 'public', 0);
