INSERT INTO `wp_eventscalendar_main` VALUES (70, 'Legal Beagle Bagel Breakfast ', 'Portable devices such as cellular phones, tablets, and computers have revolutionized how and where work gets done. But it also brought a new host of problems to the HR professional attempting to manage the workforce. When is an employee eligible for additional compensation? Who pays for the technology and connection fees? How does the employer know when an employee is working? Join us in person or via webinar, for our monthly Legal Beagle Bagel Breakfast to discuss handling these challenging issues. Call: 559.256.5000 or email: receptionist@flcz.net ', 'FLCZ Law Offices: 7112 N Fresno St., Suite 450. Fresno CA 93720', '', '2014-09-25', '09:00:00', '2014-09-25', '10:15:00', 'public', 0);
