INSERT INTO `wp_posts` VALUES (687, 1, '2013-07-09 15:20:24', '2013-07-09 23:20:24', '<strong>Title: </strong>Behavior-Based Interviewing<br /><strong>Location: </strong>FLGZ Law Office Conference Room 7112 N. Fresno St., Ste.450<br /><strong>Description: </strong>Turnover is costly, time consuming and a downright hassle.  Employers can alleviate this burden by adopting a structured hiring process focused on behavior-based interviewing.  Join us to learn how to ask the right questions, get the answers you need and make informed hiring decisions.<br /><strong>Start Time: </strong>09:00<br /><strong>Date: </strong>2013-07-18<br /><strong>End Time: </strong>12:00 Noon<br />', 'Behavior-Based Interviewing', 0, '', 'publish', 'open', 'open', '', 'behavior-based-interviewing', '', '', '2013-07-09 15:20:24', '2013-07-09 23:20:24', '', 0, 'http://localhost:8080/2013/07/behavior-based-interviewing/', 0, 'post', '', 0);
