INSERT INTO `wp_posts` VALUES (725, 1, '2009-03-05 06:26:12', '2009-03-05 14:26:12', 'Unlimited telephone support with our HR Professionals *\r\n<ul>\r\n	<li>Free monthly Legal Beagle seminars for the Company’s designated representative</li>\r\n	<li> Quarterly HR bulletins plus breaking employment news bulletins as warranted</li>\r\n	<li> Discounted consulting fees</li>\r\n</ul>\r\nIn addition, when you enroll, you’re eligible to receive a Human Resources Set-Up Audit for a reduced fee of $225 – normally a $350 value.\r\n\r\n<h4>* Unlimited Telephone Support</h4>\r\n<small>The Company’s designated representative has unlimited telephone access to our HR consultants from 8 am to 5:30 pm Monday through Friday. We encourage you to take advantage of this benefit and contact our HR consultants as needed. If your question extends beyond a telephone consult into project work, i.e. a harassment investigation or updating a policy, the HR consultant will complete any additional work you authorize at a reduced hourly fee.</small>\r\n<h3>Terms:</h3>\r\nThe term of the Business Partner Service Agreement is one-year from date of signing.\r\n\r\nFees quoted are based on one designated company representative, and the total number of employees within the organization, with a minimum monthly fee of $50 per month. Please request an HR Business Partner Package for complete terms and provisions.', 'HR Business Partner Benefits', 0, '', 'inherit', 'open', 'open', '', '27-revision-6', '', '', '2009-03-05 06:26:12', '2009-03-05 14:26:12', '', 27, 'http://localhost:8080/2009/03/27-revision-6/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES (726, 1, '2012-03-23 08:03:30', '2012-03-23 16:03:30', '<h5 style="text-align: center;">Human Resource Consulting &amp; Outsourcing Services</h5>\r\nAt Sierra HR Partners, we understand the challenges faced by business owners and employers in California. Hiring, managing and retaining employees; creating an enthusiastic and unified workforce; and complying with the myriad state and federal employment laws is overwhelming.\r\n\r\nWe can help, relieving you of the worrisome, time-consuming responsibilities of HR administration, so you can focus on growing and building your business.\r\n\r\nContact us to discuss how our human resource ("HR") consulting and outsourcing services can benefit your business.\r\n\r\n<address><strong>Sierra HR Partners, Inc.</strong>\r\n7447 N. First Street, Suite 103\r\nFresno, CA 93720\r\nTel: 559.431.8090\r\nFAX: 559.437.0500</address>', 'Who We Are', 0, '', 'inherit', 'open', 'open', '', '2-revision-26', '', '', '2012-03-23 08:03:30', '2012-03-23 16:03:30', '', 2, 'http://localhost:8080/2012/03/2-revision-26/', 0, 'revision', '', 0);
