INSERT INTO `wp_posts` VALUES (746, 1, '2013-10-07 17:54:06', '2013-10-08 01:54:06', '<strong>Title: </strong>Sexual Harassment Prevention Training<br /><strong>Location: </strong>Sierra HR Offices 7447 N. First St., Ste. 103, Fresno, CA 93720<br /><strong>Description: </strong>We recommend all employers provide this training as a proactive step in improving morale and defending against potential lawsuits.  \r\nAB 1825 requires all employers with 50 or more employees to provide two hours of interactive sexual harassment training to all supervisors every two years, effective January 1, 2006.  Our program, developed with lawyers, meets all AB 1825 requirements in a fun and collaborative forum.  <br /><strong>Start Time: </strong>9:00<br /><strong>Date: </strong>2013-12-12<br /><strong>End Time: </strong>11:15<br />', 'Sexual Harassment Prevention Training', 0, '', 'publish', 'open', 'open', '', 'sexual-harassment-prevention-training-2', '', '', '2013-10-07 17:54:06', '2013-10-08 01:54:06', '', 0, 'http://localhost:8080/2013/10/sexual-harassment-prevention-training-2/', 0, 'post', '', 0);
