INSERT INTO `wp_posts` VALUES (826, 1, '2014-08-11 14:54:19', '2014-08-11 22:54:19', '<strong>Every year we review laws, regulations, and judicial opinions affecting California employers. By conducting this review we are able to implement necessary changes to your employee hand book and current policies that keep you in compliance with current state and federal employment laws.</strong>\r\n<blockquote>\r\n<h3 style="text-align: center;"><span style="text-decoration: underline;">Changes in 2014</span></h3>\r\n</blockquote>\r\n<ul>\r\n	<li>Updated language for Arbitration Agreements</li>\r\n	<li>Additions to Equal Employment Opportunity policy for organizations receiving public funding</li>\r\n	<li>Updates to Harassment and Discrimination policy</li>\r\n	<li>New rules for overtime pay for domestic work employees</li>\r\n	<li>Recovery period to prevent Heat Illness</li>\r\n	<li>Updates to Family and Medical Leave policies: Military-related needs</li>\r\n	<li>Updates to policies regarding Victims of Crime, Domestic Violence and Stalking</li>\r\n	<li>Immigration-related policies to protect employees who file claims</li>\r\n</ul>\r\n<em>*Fees are based on actual hours spent in providing services, resulting in the most cost effective handbook and policy review process. </em>', 'Labor Law Updates', 0, '', 'inherit', 'open', 'open', '', '29-revision-14', '', '', '2014-08-11 14:54:19', '2014-08-11 22:54:19', '', 29, 'http://localhost:8080/2014/08/29-revision-14/', 0, 'revision', '', 0);
