INSERT INTO `wp_posts` VALUES (938, 1, '2015-03-24 11:06:40', '2015-03-24 19:06:40', 'The California Occupational Safety and Health Standards Board has issued an amendment to its Heat Illness Standard, which applies to all outdoor places of employment. If you have employees who work outdoors, or in an indoor environment that lacks air conditioning, it will be important to understand these changes, which are effective May 1, 2015.', 'Changes to California''s Heat Illness Standard:', 0, 'Click for full article ', 'inherit', 'open', 'open', '', 'hrbp-bulletin-march-2015', '', '', '2015-03-24 11:06:40', '2015-03-24 19:06:40', '', 29, 'http://localhost:8080/wp-content/uploads/HRBP-Bulletin-March-2015.pdf', 0, 'attachment', 'application/pdf', 0);
INSERT INTO `wp_posts` VALUES (939, 1, '2015-03-24 11:08:00', '2015-03-24 19:08:00', 'Employment records can really pile-up over the years and it may not be clear what you have to retain as an employer, and for how long. To add to the confusion, document retention is governed by multiple acts and California labor codes, including but not limited to FLSA, Title VII, FEHA, ADA, Cal-OSHA and ERISA.', 'Document Retention:', 0, 'Click here for full article ', 'inherit', 'open', 'open', '', 'hrbp-bulletin-february-2015-2', '', '', '2015-03-24 11:08:00', '2015-03-24 19:08:00', '', 29, 'http://localhost:8080/wp-content/uploads/HRBP-Bulletin-February-20151.pdf', 0, 'attachment', 'application/pdf', 0);
